import * as React from 'react';
import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, Button, TouchableOpacity, Image, ActivityIndicator } from 'react-native';
import 'react-native-gesture-handler'
import { db, authy } from '../firebase'
import * as WebBrowser from 'expo-web-browser';
import { ResponseType } from 'expo-auth-session';
import * as Google from 'expo-auth-session/providers/google';
import firebase from 'firebase';


const auth = firebase.auth();

// const auth = getAuth();
WebBrowser.maybeCompleteAuthSession();

export default function Login({navigation}) {
  const [loading, setLoading] = React.useState(true)
  const [request, response, promptAsync] = Google.useIdTokenAuthRequest({
    expoClientId:
      "946472432624-qa0rligmfbls3lf4dhbpf7mf9g2l7egv.apps.googleusercontent.com",
  });

  React.useEffect(() => {
    if (response?.type === 'success') {
      // console.log(response)
      const { id_token } = response.params;
      
      const credential = firebase.auth.GoogleAuthProvider.credential(id_token);

      auth.signInWithCredential(credential).then(data=>console.log(data));
    }
  }, [response]);


  React.useEffect(() => {
    authy.onAuthStateChanged(function(user){
      if (user) {
        const usersRef = db.collection("users").doc(user.email);

        // check if user details exist in node, if not then create
        usersRef.get()
        .then((docSnapshot) => {
          if (docSnapshot.exists) {
            console.log('exists')
            // usersRef.onSnapshot((doc) => {
            //   // do stuff with the data
            // });
          } else {
            usersRef.set({
              name: user.displayName,
              image: user.photoURL,
              email: user.email,
            }); // create the document

            db.collection("chats").doc(user.email).collection("Messages").doc();
          }
        });

        var userDetails = {
          name: user.displayName,
          email: user.email,
          image: user.photoURL
        }

        setLoading(false)
        navigation.replace("Home", {
          userDetails: userDetails
        })
        
      } else {
        setLoading(false)
      }
    })  
  }, []);

  const checkIfLoggedin = () => {
    // authy.onAuthStateChanged(function(user){
    //   if (user) {
    //     console.log(user)
    //     setLoading(false)
        navigation.replace("Home", {
          userDetails: {},
        });
      // } else {
      //   setLoading(false)
    //   }
    // })  
  }

  // checkIfLoggedin()

  return (
    <View style={styles.container}>
      {loading?(
        <View>
          <ActivityIndicator size="large" />
        </View>
      ):(
        <View style={styles.container}>
          <Image
            source={require('../assets/logoI.png')}
            style={{ width: 250, height: 250, marginBottom: 15, marginTop: -50 }}
            // transition={true}
          />
          <TouchableOpacity
            style={styles.buttonGPlusStyle}
            onPress={() => {
              promptAsync();
            }}
            disabled={!request}
            activeOpacity={0.5}
          >
            <Image
              source={{
                uri: "data:image/png;base64,",
              }}
              style={styles.buttonImageIconStyle}
            />
            <View style={styles.buttonIconSeparatorStyle} />
            <Text style={styles.buttonTextStyle}>Sign in with Google</Text>
          </TouchableOpacity>
          <StatusBar style="auto" />
        </View>
      )}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'rgb(237, 248, 250)',
    alignItems: 'center',
    justifyContent: 'center',
  },
  buttonGPlusStyle: {
    flexDirection: "row",
    alignItems: "center",
    backgroundColor: "rgb(187, 246, 255)",
    borderWidth: 0.5,
    borderColor: "#fff",
    height: 50,
    borderRadius: 15,
    margin: 5,
    // paddgin
  },
  buttonImageIconStyle: {
    padding: 15,
    margin: 8,
    height: 20,
    width: 20,
    borderRadius: 40,
    resizeMode: "stretch",
  },
  buttonIconSeparatorStyle: {
    backgroundColor: "rgb(18, 96, 108)",
    width: 1,
    height: 40,
  },
  buttonTextStyle: {
    color: "rgb(18, 96, 108)",
    marginBottom: 4,
    // marginTop: 4,
    marginLeft: 10,
    marginRight: 10,
    fontSize: 18,
    // fontWeight: "semibold",
  },
});